function myDiffusiondiff_mult;function diff_multN = 50;T = 500;p = 0.5;xbounds = [0 10];ybounds = [0 4];xdata = zeros(N,T);ydata = zeros(N,T);xdata(:,1) = mean(xbounds);ydata(:,1) = 0;xgrid = 0.01;ygrid = 0.01;for t = 1:T-1  dx = ((rand(N,1)>p)-0.5)*2;  dy = ((rand(N,1)>p)-0.5)*2;  xdata(:,t+1) = xdata(:,t)+dx*xgrid;  xdata(find(xdata(:,t+1)<xbounds(1)),t+1) = xbounds(1);  xdata(find(xdata(:,t+1)>xbounds(2)),t+1) = xbounds(2);    ydata(:,t+1) = ydata(:,t)+dy*ygrid;  ydata(find(ydata(:,t+1)<ybounds(1)),t+1) = ybounds(1);  ydata(find(ydata(:,t+1)>ybounds(2)),t+1) = ybounds(2);endfigure;handle = scatter(xdata(:,1),ydata(:,1),'filled');xlim(xbounds);ylim(ybounds);for t = 1:T  set(handle,'xdata',xdata(:,t),'ydata',ydata(:,t));  title(num2str(t));  drawnow;end  function diff_singlexbounds = [0 10];ybounds = [0 4];xdata = [mean(xbounds)];ydata = [0];xgrid = 0.01;ygrid = 0.01;figure;handle = scatter(xdata,ydata,'filled');xlim(xbounds);ylim(ybounds);p = 0.5;for t = 1:10000  dx = ((rand>p)-0.5)*2;  dy = ((rand>p)-0.5)*2;  xdata = xdata+dx*xgrid;  xdata(find(xdata<xbounds(1))) = xbounds(1);  xdata(find(xdata>xbounds(2))) = xbounds(2);    ydata = ydata+dy*ygrid;  ydata(find(ydata<ybounds(1))) = ybounds(1);  ydata(find(ydata>ybounds(2))) = ybounds(2);    set(handle,'xdata',xdata,'ydata',ydata);  drawnow;end